package com.overlading;

public class Circle {

	private int radius;
	
	
	//Constructor overloading
	public Circle() {
		// TODO Auto-generated constructor stub
	}
	
	public Circle(int radius) {
		this.radius = radius;
	}
	
	public void setRadius(int radius) {
		this.radius = radius;
	}
	public int getRadius() {
		return radius;
	}
	
}
